import { useQuery, gql } from '@apollo/client';
// import React, { Component } from 'react'
import React, {useEffect, useState} from 'react';
import logohere from '../Images/logohere.png'
const ALL_USERS = gql`
  query {
    getAllUsers {
      id
      firstName
      lastName
      email
      password
    }
  }
`;
function TopMenu(){
    const { data } = useQuery(ALL_USERS , {fetchPolicy: "cache-and-network"})
    const [users, setUsers] = useState({});
    const [searchField, setSearchField] = useState({});

 
 const handleChange=(value)=>{
        setSearchField({searchField:value});
        console.log("setSearchField", searchField)
 }
 useEffect(() => {
        fetch(data)
        .then(response => response.json())
        .then(users => setUsers({users:users}));
 });
    //  }
    //      componentDidMount(){
    //         // 
    //      }

    return (
        <div className="top-menu is-shadow-2">
            <div className="menu-logo-container">
                <img className="menu-logo" src={logohere} alt="Logo" />
            </div>
            <div>
                <input
                    className="search-field"
                    placeholder="Search here...."
                    type="search"
                    name="name"
                    autoComplete="off"
                    onChange={handleChange}
                />
            </div>
        </div>
    );

}
export default TopMenu;
