import React from 'react';
import UserCard from './UserCard'
import { useQuery, gql, NetworkStatus } from '@apollo/client';

const ALL_USERS = gql`
  query {
    getAllUsers {
      id
      firstName
      lastName
      email
      password
    }
  }
`;

function Users ({searchField}){


    //Passing query to useQuery hook to fetch data
    const{data, loading, error, networkStatus} = useQuery(ALL_USERS, {fetchPolicy: "cache-and-network"})


    if (networkStatus === NetworkStatus['refetch'])
        return 'Refetching!';
    else if (loading)
        return 'Loading..';
    else if (error)
        return `Error! ${error}`;
    else if(data)
        console.log(data)
        return(
            <div className="is-scrollable-list">
                {/* {data && Object.values(data).filter(user => {
                    if (searchField == ""){
                        return user
                    }
                    else if (user.firstName.toLowerCase().includes(searchField.toLowerCase())){
                        return user
                    }
                }).map((user) => (
                        <UserCard
                            key={user.id}
                            item={user}
                        />
                    ))} */}
            </div>
        )
}

export default Users;
