import { useQuery, gql } from '@apollo/client';
import React, { Component } from 'react'
import { useState } from 'react/cjs/react.production.min';
import logohere from '../Images/logohere.png'
const ALL_USERS = gql`
  query {
    getAllUsers {
      id
      firstName
      lastName
      email
      password
    }
  }
`;
function TopMenu(){
    const [users, setUsers] = useState();
    const [searchField, setSearchField] = useState();

 
 const handleChange=(value)=>{
        setSearchField({searchField:value});
        const { data } = useQuery(ALL_USERS , {fetchPolicy: "cache-and-network"})
        fetch(data)
        .then(response => response.json())
        .then(users => setUsers({users:users}));
 }
    //  }
    //      componentDidMount(){
    //         // 
    //      }

    return (
        <div className="top-menu is-shadow-2">
            <div className="menu-logo-container">
                <img className="menu-logo" src={logohere} alt="Logo" />
            </div>
            <div>
                <input
                    className="search-field"
                    placeholder="Search here...."
                    type="search"
                    name="name"
                    autoComplete="off"
                    onChange={this.handleChange}
                />
            </div>
        </div>
    );

}
export default TopMenu;
