import {Link} from "react-router-dom";
import Logo from '../Images/logohere.png'
import UserDetails from "./UserDetails";
import { useQuery, gql, NetworkStatus } from '@apollo/client';

const ALL_USERS = gql`
  query {
    getAllUsers {
      id
      firstName
      lastName
      email
      password
    }
  }
`;

function UserCard({item}){
    // const{data, loading, error, networkStatus} = useQuery(ALL_USERS, {fetchPolicy: "cache-and-network"})
    return (
        <Link
            to={"/User"}
            className={"card-margin align-left is-row is-card"}
        >
            <div className="is-50">
                <div className="image-container">
                    <img className="card-logo" src={Logo} alt="Logo" />
                </div>
            </div>
            <div className="is-50">
                {/* <h3>{item.firstName} - {item.lastName}</h3> */}
                {/* {data ? data.getAllUsers.map((item) => (
                <UserDetails 
                item={item}
                />
                )) : 'Users list undefined...' } */}
            </div>
        </Link>
    );
}

export default UserCard
